package com.arssycro.advent2022.day06

fun main() {
    Day06a().process()
}

class Day06a {
    fun process() {
        val input =
            ""
        input.forEachIndexed { index, c ->
            if (index > 3) {
                val set = input.substring(index - 4, index).toCharArray().toSet()
                if (set.size == 4) {
                    println(index)
                    return
                }
            }
        }
    }
}