package com.arssycro.advent2022.day06

fun main() {
    Day06b().process()
}

class Day06b {
    fun process() {
        val input =
            ""
        input.forEachIndexed { index, c ->
            if (index > 13) {
                val set = input.substring(index - 14, index).toCharArray().toSet()
                if (set.size == 14) {
                    println(index)
                    return
                }
            }
        }
    }
}